window.epkbErrorsList=[];var showNoticeTimeout=true;var previousErrorHandler=window.onerror;var jqueryChecked=0;window.onerror=function(r,e,o,t,i){if(i.name!="NS_ERROR_FAILURE"){epkbErrorsList.push({msg:r,url:e})}if(showNoticeTimeout){setTimeout(epkbShowErrorNotices,2e3);showNoticeTimeout=false}if(typeof jQuery!=="undefined"){jQuery(document).trigger("epkb_js_error",[r,e,o,t,i])}if(previousErrorHandler){return previousErrorHandler(r,e,o,t,i)}return false};function epkbShowErrorNotices(){if(typeof jQuery=="undefined"||jQuery(".epkb-js-error-notice").length==0){setTimeout(epkbShowErrorNotices,1e3);if(jqueryChecked>20){return}jqueryChecked++;return}jQuery(".epkb-js-error-notice").hide("fast");let r;for(r of epkbErrorsList){jQuery(".epkb-js-error-notice").find(".epkb-js-error-msg").text(r.msg);jQuery(".epkb-js-error-notice").find(".epkb-js-error-url").text(r.url);jQuery(".epkb-js-error-notice").show("fast")}jQuery(".epkb-js-error-close").on("click",(function(){jQuery(this).closest(".epkb-js-error-notice").hide("fast")}))}